mapper.delete:
delete from
cy_business_flow_table
where sold_year = #{soldYear,jdbcType=VARCHAR} 
and sold_month = #{soldMonth,jdbcType=VARCHAR} 
and file_type = #{fileType,jdbcType=VARCHAR}

mapper.update:
update cy_business_flow_table SET type=1,update_time=#{sysTime} WHERE
flow_id in

#{item.flowId}

mapper.select:
SELECT flow_id,f.accept_unit_id,f.product_id,is_terminal,(SELECT `name` FROM cy_merchandiser_table WHERE merch_id=f.sold_unit_id) as out_unit,(CASE WHEN is_terminal=1 THEN (SELECT hospital_name FROM cy_hospital_table WHERE hospital_id=f.accept_unit_id) ELSE (SELECT `name` FROM cy_merchandiser_table WHERE merch_id=f.accept_unit_id) END) as accept_unit,(SELECT customer_name FROM cy_customer_table WHERE customer_id=f.customer_id) as customer_name, (CASE f.is_terminal WHEN 1 THEN (select rebate FROM cy_customer_protocol_table WHERE customer_id=f.customer_id AND hospital_id=f.accept_unit_id AND product_id=f.product_id) ELSE 0 END) as sec_expense ,sold_date, product_name,product_norms,product_price,(CASE WHEN is_terminal=1 THEN sold_goods_num ELSE allocate_goods_num END) as amount,(CASE WHEN is_terminal=1 THEN (SELECT promotion_expense FROM cy_customer_protocol_table cp WHERE cp.customer_id=f.customer_id AND cp.hospital_id=f.accept_unit_id AND cp.product_id=f.product_id) ELSE NULL END) as promotion_expense, (CASE WHEN is_terminal=1 THEN sold_price ELSE allocate_price END) as sold_price,sold_money,flow_flag,department,remark,create_time FROM cy_business_flow_table f LEFT JOIN cy_product_table p ON f.product_id=p.product_id where 1>0
and is_terminal=#{isTerminal}
and flow_flag=#{flowFlag}
and (CASE is_terminal WHEN 1 THEN (SELECT hospital_name FROM cy_hospital_table WHERE hospital_id=f.accept_unit_id) ELSE (SELECT `name` FROM cy_merchandiser_table WHERE merch_id=f.accept_unit_id) END) LIKE CONCAT('%',#{acceptUnit,jdbcType=VARCHAR},'%')
and batch_no like CONCAT('%',#{batchNo,jdbcType=VARCHAR},'%')
and sold_date>#{startDate}
and sold_date<#{endDate}
order by sold_date asc,flow_id asc limit #{firstItem},#{pageSize}

		SELECT count(0)	FROM cy_business_flow_table f LEFT JOIN cy_product_table p ON f.product_id=p.product_id 
		where 1>0
 		
 		and is_terminal=#{isTerminal}
 		
 		
 		and flow_flag=#{flowFlag}
 		
 		
 		and (CASE is_terminal	WHEN 1 THEN	(SELECT hospital_name FROM cy_hospital_table WHERE hospital_id=f.accept_unit_id) 
		ELSE	(SELECT `name` FROM cy_merchandiser_table WHERE merch_id=f.accept_unit_id)	END)	
		LIKE CONCAT('%',#{acceptUnit,jdbcType=VARCHAR},'%')
 		
 		
 		and batch_no like CONCAT('%',#{batchNo,jdbcType=VARCHAR},'%')
 		
 		
 		and sold_date>#{startDate}
 		
 		
 		and sold_date<#{endDate}
