mapper.select:
select

from cy_impfile_record_table
where imp_year = #{impYear,jdbcType=VARCHAR}
and imp_month = #{impMonth,jdbcType=VARCHAR}
and file_type = #{fileType,jdbcType=VARCHAR}

mapper.delete:
delete from cy_impfile_record_table
where imp_year = #{impYear,jdbcType=VARCHAR}
and imp_month = #{impMonth,jdbcType=VARCHAR}
and file_type = #{fileType,jdbcType=VARCHAR}

mapper.update:
update cy_impfile_record_table

	
		file_name = #{fileName,jdbcType=VARCHAR},
	

where imp_year = #{impYear,jdbcType=VARCHAR}
and imp_month = #{impMonth,jdbcType=VARCHAR}
and file_type = #{fileType,jdbcType=VARCHAR}

update cy_impfile_record_table
set file_name = #{fileName,jdbcType=VARCHAR}
where imp_year = #{impYear,jdbcType=VARCHAR}
and imp_month = #{impMonth,jdbcType=VARCHAR}
and file_type = #{fileType,jdbcType=VARCHAR}
